section {
  background: var(--color-brand);
  overflow: hidden;

  &.image-left {
    figure {
      grid-column: 1;
      grid-row: 1;
    }
    .body {
      grid-column: 2;
    }
  }
}

.section-container {
  display: flex;
  flex-direction: column-reverse;
  gap: 2rem;

  @media (min-width: 800px) {
    display: grid;
    grid-template-columns: 1fr 1fr;
    place-items: center;
    gap: 6rem;
    padding-block: 6rem;
  }
}

.body {
  color: var(--color-inverted);
  display: grid;
  place-items: start;
  padding-top: 2rem;

  .headline {
    color: var(--color-heading);
    font-family: var(--font-heading);
    font-weight: 700;
    font-size: clamp(2.75rem, 10vw, 3rem);
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  .subheading {
    font-weight: 400;
    font-size: 1.125rem;
    margin-bottom: 2rem;
  }

  .button {
    font-weight: 500;
  }
}

figure {
  position: relative;

  img {
    border-radius: var(--border-radius);
    z-index: 1;
    position: relative;
  }
}

